        PRAGMA foreign_keys=ON;
        CREATE TABLE warehouses ( _id INTEGER PRIMARY KEY, name TEXT NOT NULL, location TEXT NOT NULL);
        CREATE TABLE items ( _id INTEGER PRIMARY KEY, name TEXT NOT NULL, condition TEXT NOT NULL);
 		CREATE TABLE inactive (warehouse INTEGER, item INTEGER, amount INTEGER, FOREIGN KEY(warehouse) REFERENCES warehouses(_id) ON DELETE CASCADE ON UPDATE CASCADE, FOREIGN KEY(item) REFERENCES items(_id) ON DELETE CASCADE ON UPDATE CASCADE);
 		CREATE TABLE active (warehouse INTEGER, item INTEGER, amount INTEGER, time TEXT NOT NULL, FOREIGN KEY(warehouse) REFERENCES warehouses(_id) ON DELETE CASCADE ON UPDATE CASCADE, FOREIGN KEY(item) REFERENCES items(_id) ON DELETE CASCADE ON UPDATE CASCADE);

 		CREATE TABLE persons ( _id INTEGER PRIMARY KEY, name TEXT NOT NULL, role TEXT NOT NULL, phone TEXT UNIQUE);
		CREATE TABLE lent (person INTEGER, amount INTEGER NOT NULL, time TEXT NOT NULL, FOREIGN KEY(person) REFERENCES persons(_id) ON DELETE CASCADE ON UPDATE CASCADE);

